$(function(){$("#add-billing-address, #add-shipping-address").hide();$(document).on("click","#new-billing-address",function(a){a.preventDefault();$("#current-billing-address").hide();$("#add-billing-address").slideDown()});$(document).on("click","#new-shipping-address",function(a){a.preventDefault();$("#current-shipping-address").hide();$("#add-shipping-address").slideDown()});$(document).on("click","#btnResetBilling",function(){$("#current-billing-address").slideDown();$("#add-billing-address").hide()});$(document).on("click","#btnResetShipping",function(){$("#current-shipping-address").slideDown();$("#add-shipping-address").hide()});$(document).on("change","#shipping_types",function(){var a=$(this).val();$("#btnChooseShippingType").attr("disabled","disabled");if(a!==0)if(confirm("Are you sure you want to upgrade to "+toTitleCase(a.replace(/_/g," "))+"?"))window.location.href="/Cart/UpgradeShipping?type="+a;else $("#shipping_types").val(0);$("#btnChooseShippingType").removeAttr("disabled")});$("label[for=shipping_types]").show()});