var content_height=0,max_height=0;function changePartTab(a){if(a.length>0){$(".part_tab_container ul li a").removeClass("active");$(".part_content").fadeOut();if(a.indexOf("#")===-1)a="#"+a;$("a[href="+a+"]").addClass("active");$(a).fadeIn();$(".part_container").css("height",$(a).height()+30)}else{$(".part_tab_container ul li a").removeClass("active");$(".part_tab_container ul li a:first").addClass("active");if($(".part_content:first").css("display")!=="block"){$(".part_content").fadeOut();$(".part_content:first").fadeIn();$(".part_container").css("height",$(".part_content:first").height()+30)}}}$(function(){Shadowbox.init();$("#vehicles table").dataTable({bFilter:false,bPaginate:false});$(".part_tab_container").show();$(".part_content").hide();$(".part_content:first").show();$(".part_container").css("min-height",$(".part_content:first").height()+30);$(".part_content").css("position","absolute").css("left","0").css("top","0");$("#vehicles table tr").mouseover(function(){$(this).css("cursor","pointer")});$("#vehicles table tbody tr").live("click",function(){var d,c,a,b=0;d=$(this).find("td:nth-child(1)").html();c=$(this).find("td:nth-child(2)").html();a=$(this).find("td:nth-child(3)").html();b=$(this).find("td:nth-child(4)").html();window.location.href=window.location.protocol+"//"+window.location.host+"/Lookup/"+encodeURIComponent(d)+"/"+encodeURIComponent(c)+"/"+encodeURIComponent(a)+"/"+encodeURIComponent(b.replace(/\//g,"!"))});$(window).bind("hashchange",function(){var a=$.param.fragment();changePartTab(a)});$(window).trigger("hashchange")});